import React, { useEffect, useState } from 'react';

function App() {
  // Estado inicial: Inicializa 'backendData' como una matriz que contiene un objeto vacío.
  const [backendData, setBackendData] = useState([{}]);

  // Efecto de carga: Realiza una solicitud a la API cuando el componente se monta.
  useEffect(() => {
    fetch("/api") // Realiza una solicitud a la API en el servidor.
      .then(response => response.json()) // Convierte la respuesta en JSON.
      .then(data => {
        // Actualiza el estado 'backendData' con los datos recibidos de la API.
        setBackendData(data);
      });
  }, []); // El segundo argumento vacío [] asegura que el efecto se ejecute solo una vez al montar el componente.

  // Renderizado condicional: Muestra diferentes elementos en función del estado de 'backendData'.
  return (
    <div>
      {(typeof backendData.user === 'undefined') ? (
        // Si 'backendData.user' es indefinido, muestra "Loading...".
        <p>Loading...</p>
      ) : (
        // Si 'backendData.user' está definido, mapea los usuarios y muestra cada uno en un elemento <p>.
        backendData.user.map((user, i) => (
          <p key={i}>{user}</p>
        ))
      )}
    </div>
  );
}

export default App;
