import React, {useState} from 'react';

import FormInput, {CheckRegex} from './components/FormInput.jsx';

const Comadreja = () => {

	const [values, setValues] = useState({
		nombre:"",
		email:"",
		telefono:"",
		cedula:""
	});

	const [valuesErr, setValuesErr] = useState({
		nombre:false,
		email:false,
		telefono:false,
		cedula:false
	});

	const inputs = [
		{ id:1,
			name:"nombre",
			type:"text",
			placeholder:"user name",
			label:"Username",
			errorMessage: "min. 3 letras, solo se permiten '&','-','_','.' y espacios",
			required: true,
			patron: /^([0-9]{10})$/ },
		{ id:2,
			name:"email",
			type:"text",
			placeholder:"email",
			label:"Email",
			errorMessage: "ejemplo@dominio.com",
			required: true,
			patron: /^([876][0-9]{3}\-[0-9]{4})$/ },
		{ id:3,
			name:"telefono",
			type:"text",
			placeholder:"telefono",
			label:"Telefono",
			errorMessage:"1234-5678",
			required: true,
			patron: /^([876][0-9]{3}\-[0-9]{4})$/ },
		{ id:4,
			name:"cedula",
			type:"text",
			placeholder:"cedula",
			label:"Cedula",
			errorMessage:"Solo numeros, 10 digitos",
			required: true,
			patron: /^([0-9]{10})$/},
	]

  const onChange = (e) => {
		setValues({ ...values, [e.target.name]: e.target.value });
	}

	return (
		<div className="App">
			<form>
				<h1>hola</h1>
					{inputs.map((input) => (
						<FormInput
							key={input.id}
							{...input}
							value={values[input.name]}
							comadreja={valuesErr[input.name]}
							onChange={onChange}
						/>
					))}
				<button onClick={handleSubmit}>submit</button>
			</form>
		</div>
	);
};

const handleSubmit = (e, inputs, values, setValuesErr, CheckRegex) => {
  	e.preventDefault();
  	const updatedValuesErr = {};
	  const checkInputs = inputs.map((input) => {
	    if ( input.required && CheckRegex(values[input.name], input.patron) ) {
	    	updatedValuesErr[input.name] = false;
	    } 
	    else if ( !input.required && (values[input.name] === '' || CheckRegex(values[input.name], input.patron) )){
	    	updatedValuesErr[input.name] = false;
	    }
	    else {
	    	updatedValuesErr[input.name] = true;
	    }
  	});
  	setValuesErr(updatedValuesErr); 
  	const hasFalseValue = Object.values(valuesErr).includes(false);
};
export default Comadreja;
