 return (
    <div className="mb-3">
      <input
        className={`form-control form-control-lg${boolError ? ' is-invalid' : ''}`}
        {...inputProps}
        onChange={onChange}
      />
      {boolError && <div className="invalid-feedback">{errorMessage}</div>}
    </div>
  );
};

