import { useState } from "react";
import "./formInput.css";

const FormInput = (props) => {
  const { label, errorMessage, onChange, id, comadreja, ...inputProps } = props;

  return (
    <div className="formInput">
      <label>{label}</label>
      <input
        {...inputProps}
        onChange={onChange}
      />
      {comadreja && <span>{errorMessage}</span>}
    </div>
  );
};


const CheckRegex = (value, pattern) => {
  // Use the pattern to check the value and return true/false
  return pattern.test(value);
}

export default FormInput;
export { CheckRegex };
