import React, {useState} from 'react';

import FormInput from './components/FormInput.jsx';


const Comadreja = () => {

	 const [formFieldValues, setFormFieldValues] = useState({
    nombre: "",
    email: "",
    telefono: "",
    cedula: ""
  });

	const inputs = [
		{
			id:1,
			name:"nombre",
			type:"text",
			placeholder:"user name",
			label:"Username",
			errorMessage: "min. 3 letras, solo se permiten '&','-','_','.' y espacios",
			required: true,
			pattern: '^([a-zA-Z0-9&\\\-_.]{3,}[a-zA-Z0-9 &\\\-_.]*)$'
		},
		{
			id:2,
			name:"email",
			type:"text",
			placeholder:"email",
			label:"Email",
			errorMessage: "ejemplo@dominio.com",
			required: true,
			pattern: '[a-zA-Z0-9@_\-\.%]{1,}@[a-zA-Z0-9_\-]+(\.[a-zA-Z]{2,})+$'
		},
		{
			id:3,
			name:"telefono",
			type:"text",
			placeholder:"telefono",
			label:"Telefono",
			errorMessage:"1234-5678",
			required: true,
			pattern: '^([876][0-9]{3}\-[0-9]{4})$'

		},
		{
			id:4,
			name:"cedula",
			type:"text",
			placeholder:"cedula",
			label:"Cedula",
			errorMessage:"Solo numeros, 10 digitos",
			required: true,
			pattern: '^([0-9]{10})$'
		},
	]

	const handleSubmit = (e) => {
    e.preventDefault();
    // Update the 'values' state with the current form field values
    setValues({ ...formFieldValues });
  };

   // investigar mas como funciona esto
   //const data = new FormData(e.target);
   //console.log(Object.fromEntries(data.entries()));

  const onChange = (e) => {
    // Update the 'formFieldValues' state with the current field value
    setFormFieldValues({ ...formFieldValues, [e.target.name]: e.target.value });
  };

  const [values, setValues] = useState(formFieldValues);

  console.log(values);

	return (
		<div className="App">
			<form onSubmit={handleSubmit}>
				<h1>hola</h1>
					{inputs.map((input) => (
						<FormInput
							key={input.id}
							{...input}
							value={values[input.name]}
							onChange={onChange}
						/>
					))}
				<button>submit</button>
			</form>
		</div>
	);
};

export default Comadreja;
