import React, {useState} from 'react';
import './index.css';
import 'bootstrap/dist/css/bootstrap.css';
import validarInput from './validar'

function App() {
  {/*variables para verificar*/}
  const [empresaNombre, setEmpresaNombre] = useState("");
  const [empresaNombreErr, setEmpresaNombreErr] = useState(false);

  const [empresaEmail, setEmpresaEmail] = useState("");
  const [emailEmpresaErr, setEmpresaEmailErr] = useState(false);

  {/*funcion para verificar datos*/}
  const datosValidos = () => {

    const emailRegex = /[a-zA-Z0-9\@\_\-\.\%]+@[a-zA-Z0-9\_\-\.]+\.[a-zA-Z]{2,}/
    const isValidEmail = validarInput(emailRegex, empresaEmail);
    setEmpresaEmailErr(isValidEmail ? false : true);


    console.log(isValidEmail)
  };

  return (
    <div className="container vh-100 d-flex justify-content-center align-items-center rounded">
      <div className="card shadow text-center p-3">
        <form>
          <div className="card-title">
            <h2 className="display-4">Registrar Empresa</h2>
            {emailErr && <h1>Ingrese los datos correctamente!</h1>}
          </div>
          <div className="card-body">
            <div className="mb-3">
              <input className="form-control form-control-lg" type="text" id="empresaNombre" placeholder="Nombre de la empresa"/>
            </div>
            <div className="row">
              <div className="col-md-6">
                <input className="form-control mb-3" type="text" value={empresaEmail} onChange={(e) => setEmpresaEmail(e.target.value)} id="empresaEmail1" placeholder="correo 1"/>
              </div>
              <div className="col-md-6">
                <input className="form-control mb-3" type="email" id="empresaEmail2" placeholder="correo 2"/>
              </div>
            </div>
            <div className="row">
              <div className="col-md-6">
                <input className="form-control mb-3" type="tel" id="empresaTelefono1" placeholder="telefono 1"/>
              </div>
              <div className="col-md-6">
                <input className="form-control mb-3" type="tel" id="empresaTelefono2" placeholder="telefono 2"/>
              </div>
            </div>
            <div className="mb-3">
              <input className="form-control" type="text" id="empresaCedula" placeholder="Cedula Juridica"/>
            </div>
            <div className="row">
              <button type="button" className="btn btn-primary btn-lg" onClick={datosValidos}>Siguiente</button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}

export default App;
