import React from "react";
import FormInput, {handleSubmit} from './FormInput'

export const FormEmpresa = ({ formData, setForm, errForm, setErrForm, navigation }) => {

	const inputs = [
		{
		    id:1,
			type:"text",
			placeholder:"Nombre Empresa",
			errorMessage: "min. 3 letras, solo se permiten '&','-','_','.' y espacios",
			required: true,
			patron: /^([a-zA-Z0-9\&\-\_\.]{3,}[a-zA-Z0-9\ \&\-\_\.]*)$/,},
		{
		    id:2,
			type:"text",
			placeholder:"Email empresarial",
			errorMessage: "ejemplo@dominio.com",
			required: true,
			patron: /^([876][0-9]{3}\-[0-9]{4})$/ },
		{
		    id:3,
			type:"text",
			placeholder:"email-opcional",
			errorMessage: "ejemplo@dominio.com",
			required: false,
			patron: /^([876][0-9]{3}\-[0-9]{4})$/ },
		{
		    id:4,
			type:"text",
			placeholder:"telefono",
			errorMessage:"1234-5678",
			required: true,
			patron: /^([876][0-9]{3}\-[0-9]{4})$/ },
		{
		    id:5,
			type:"text",
			placeholder:"telefono-opcional",
			errorMessage:"1234-5678",
			required: false,
			patron: /^([876][0-9]{3}\-[0-9]{4})$/ },
		{
		    id:6,
			type:"text",
			placeholder:"cedula juridica",
			errorMessage:"Solo numeros, 10 digitos",
			required: true,
			patron: /^([0-9]{10})$/},
	]

	const checkInputs = () => {
		let allGood = handleSubmit(inputs, values, setValuesErr, valuesErr)
		if (allGood) {
			navigation.next();
		}
	}

	return (
		<div className="container col-5 align-middle position-static">
			<div className="card shadow ">
				<div className="card-body">
				 	<h1 className="card-title">Registro Empresa</h1>
			      <form>
			        {inputs.map((input) => (
			          <FormInput
			            key={input.id}
			            {...input}
			            value={formData[input.name]}
			            boolError={errForm[input.name]}
			            onChange={setForm[input.name]}
			          />
			        ))}
		      	</form>
		      	  <div className="row">
		      			<button onClick={checkInputs} className="btn btn-primary btn-lg">next</button>
		      		</div>
		      </div>
		  </div>
		</div>
	);
};
